import './App.css';
import { useState } from 'react';
import axios from 'axios';
import { Router , Route, Switch } from "react-router-dom";

function App() {
        <div className="root">
          <Switch>
            <Route path="/" component={MainPage} exact />
            <Route path="/dashboard" component={DashBoard} exact />
            <Route path="/docs" component={Docs} exact />
            <Route component={ErrorPage} />
          </Switch>

        </div>
}

function Payment() {
  const [status, setStatus] = useState(false)


  const onSubmit = () => {
    axios.post("http://127.0.0.1:8000/payment").then(res => {
      if(res.status === 200) {
        console.log(res.data.response.callback_url)
        // window.location.replace(res.data.response.callback_url)
      }
    })
  }
  return (
    <>

<div>
      Barang: Sepatu
    </div>
    <div>
      Harga: 25000
    </div>
    <div>
      Currency: IDR
    </div>
    <div>
      Pembayaran: OVO
    </div>
    <div>
      No HP: +628123123123
    </div>
    <div>
      Address: Pluit Jakarta Selatan
    </div>
    <button onClick={() => onSubmit()}>
    Bayar
    </button>
    {status && (
      <>
      Sudah terbayar
      </>
    )}
    </>
  );
}

function Success() {
  return (
    <>
    Successfully Pay
    </>
  )
}


export default App;
