import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import axios from 'axios';
import {Route, IndexRoute} from 'react-router';

function App() {
                <Route
                    path="/"
                    ex
                    element={ <Payment /> }
                />
                {/* The next line is very important for the Navigate component to work */}
                <Route
                    path="/success"
                    com={ <Success /> }
                />
}

function Payment() {
  const [status, setStatus] = useState(false)


  const onSubmit = () => {
    axios.post("http://127.0.0.1:8000/payment").then(res => {
      if(res.status === 200) {
        console.log(res.data.response.callback_url)
        // window.location.replace(res.data.response.callback_url)
      }
    })
  }
  return (
    <>

<div>
      Barang: Sepatu
    </div>
    <div>
      Harga: 25000
    </div>
    <div>
      Currency: IDR
    </div>
    <div>
      Pembayaran: OVO
    </div>
    <div>
      No HP: +628123123123
    </div>
    <div>
      Address: Pluit Jakarta Selatan
    </div>
    <button onClick={() => onSubmit()}>
    Bayar
    </button>
    {status && (
      <>
      Sudah terbayar
      </>
    )}
    </>
  );
}

function Success() {
  return (
    <>
    Successfully Pay
    </>
  )
}


export default App;
